import React from 'react';
import './Nav.css';

const Nav = () => {


    const menu = () => {
        document.getElementById('menu');
        document.addEventListener('click', () => {

            if (event.target.matches('.show')) {
                menu.removeAttribute('hidden');
                menu.classList.add('slideInDown');
            }

            if (event.target.matches('.hide')) {
                menu.setAttribute('hidden', 'true');
                menu.classList.remove(slideInDown);
            }

        }, false);
    }

    return(
        <div class="nav">
            <div class="hamburger">
                <span class="line line1 show"></span>
                <span class="line line2 hide"></span>
            </div>
            <div id="menu" class="animated" hidden>
            <h3>Menu</h3>
            <ul>
                <li><a href="#secondPage">Who I Am</a></li>
                <li><a href="#thirdPage">Things I Do</a></li>
                <li><a href="#seventhPage">Talk To Me</a></li>
            </ul>
            </div>
        </div>
    )
}

export default Nav;
